SELECT COUNT(DISTINCT p.pipeline_id) FROM pipeline p JOIN campaign c ON p.campaign_id = c.campaign_id JOIN client_company cc ON c.client_company_id = cc.client_company_id LEFT JOIN pipeline_influencer_client_manager picm ON p.pipeline_id = picm.pipeline_id AND picm.client_manager_id IS NOT NULL LEFT JOIN client_manager cm ON cm.client_manager_id = picm.client_manager_id LEFT JOIN pipeline_user pu ON pu.pipeline_id = p.pipeline_id LEFT JOIN user u ON pu.user_id = u.user_id JOIN pipeline_status ps ON p.pipeline_status_id = ps.pipeline_status_id WHERE p.pipeline_step_id = #{pipelineStepId} AND p.is_deleted = 'N'
AND p.name LIKE CONCAT('%', #{req.keyword}, '%')
AND cc.client_company_name LIKE CONCAT('%', #{req.keyword}, '%')
AND cm.name LIKE CONCAT('%', #{req.keyword}, '%')
AND u.user_id = #{req.userId}
AND p.pipeline_status_id = #{req.filter}

SELECT DISTINCT category_id
FROM hashtag_influencer_campaign
WHERE campaign_id = #{campaignId}

